import React from  "react";
import BarChart from "./BarChart";

class BarData extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          year: {},
          yearlyData:{} 
        };
        this.handleChange = this.handleChange.bind(this);
        let years = [];
        if(props.DataSets) {
        for (var i = 0; i < props.DataSets.length; i++) {
          var date = props.DataSets[i].launchDate;
          var dataObject = new Date(date);
          if (dataObject.getFullYear()) {
            years.push(dataObject.getFullYear());        
          }
        }
      }
       this.yearlyData = years.reduce(function (object, satCount) {
        if (!object[satCount]) {
          object[satCount] = 1;
        } else {
          object[satCount]++;
        }
        return object;
      }, {});
      }
      handleChange(e) {
        let title = e.target.value;
        let data = this.yearlyData;
        console.log(data)
        let year = data.filter((d) => {
          return d.title == title;
        });
        this.setState({
          year: year,
        });
      }
      render() {
        let data = this.yearlyData;
        let selectops = data.map((data, i) => (
          <option key={data.title} value={data.title}>
            {data.title}
          </option>
        ));

        return (
          <div>
            <div>
              <select
                name="Select year"
                className="year_select"
                onChange={this.handleChange}
              >
                <option>Select Event</option>
                {selectops}
              </select>
            </div>
            <BarChart event={this.state.event} />
          </div>
        );
      }
    }
    export default BarData;
