import React from "react";
import './style.css';
//import { render } from "react-dom";

//import Header from "./components/Header"
//import Main from "./components/Main"
//import Footer  from "./components/Footer"

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            nasa : [],
            satellites : [],
            category : [],
            isLoaded : false,
        };
    }

    fetchData(url,url1){
        Promise.all([
            fetch(url)
            .then((response) => response.json()),
 /**            fetch(url1,{
                "method": "GET",
                "headers": {
                    "x-rapidapi-host": "uphere-space1.p.rapidapi.com",
                    "x-rapidapi-key": "7c38e0936bmsh43070bf163c0855p15038cjsn373b4d98e3a6"
                }
            }) **/
            fetch(url1)
            .then((response) => response.json())
        ])       
        .then((data) => {
          return data;
          // console.log(data);

        })
        .then((data) => {
            this.setState({
                nasa : data[0].collection.items[1].href,
                satellites : data[1].info.satcount,
                category : data[1].info.category,
                isLoaded: true,
            });
        })
      // .catch((error) = console.log(error));
    }
    componentDidMount() {
    const url = 'https://images-api.nasa.gov/asset/ISS%204K%20Crew%20Earth%20Observations';
    //const url1 = 'https://swapi.dev/api/people/';  
    const url1 = 'https://www.n2yo.com/rest/v1/satellite/above/41.702/-76.014/0/70/18/&apiKey=TY7W6H-2YWZWQ-9W9WEL-4FIH';
    //const url1 = 'https://uphere-space1.p.rapidapi.com/satellite/list?country=IND&page=1';

    this.fetchData(url,url1);
} 
render() {
    const { nasa,satellites,category,isLoaded } = this.state;
    return (
        <React.Fragment>
            <h1>Satellite Tracker</h1>
            {!isLoaded && <p>Loading...</p>}

            <video autoPlay controls muted loop
                 src = {nasa} type = "video/mp4" /> 

            <h3>Satellite List = {satellites} </h3> 
            <h3>Category = {category} </h3>                     
             </React.Fragment> 


      )
}

}
export default App
