import React from "react";
import Slider from "./SliderImage"

class Landing extends React.Component {
  constructor() {
    super();
    this.state = {
      imgtags: [],
      caption:[],
    };
  }

  componentDidMount() {
    //const NASA_API_KEY = process.env.REACT_APP_NASA_API_KEY;
    const latest = new Date();
    latest.setDate(latest.getDate() - 1);
    function pad(s) {
      return s < 10 ? "0" + s : s;
    }
    const latest_string = [
      latest.getFullYear(),
      pad(latest.getMonth() + 1),
      pad(latest.getDate()),
    ].join("-");

    const image_dates = [
      latest.getFullYear(),
      pad(latest.getMonth() + 1),
      pad(latest.getDate()),
    ].join("/");
    let images = [];
    fetch(
      `https://api.nasa.gov/EPIC/api/natural/date/${encodeURIComponent(
        latest_string
      )}?api_key=twk4KeZwwqGPWJFY9ksFShKEyoDGimDlJAjwDH0z`
    )
      .then((response) => response.json())
      .then((data) => {
        for (let d of data) {
          //console.log(d)
          images.push(d.image);
        }
        const l_caption = data[0].caption
      })
      .then(() => {
        let l_imgtags = images.map((i) => (         
              "https://epic.gsfc.nasa.gov/archive/natural/" +
              image_dates +
              "/png/" +
              i +
              ".png"                 
        ));     
       //console.log(l_imgtags)
        return l_imgtags;
      })
      .then((l_imgtags) => {
        console.log("inside fetch chain");
        this.setState({ 
          imgtags: l_imgtags,
          caption:l_caption,
        });
       // console.log(this.state.imgtags);
      })
      .catch((error) => {
        console.log("Request failed: ", error);
      });
    }


  render() {

    return (
      <div className = "landing">
        <div className = "landing-container">
          <div className = "lgrid-item1">
        <Slider images ={this.state.imgtags}/>
        </div>
        <div className = "lgrid-item2">
          Satellite Tracker
        </div>
        {this.state.caption}
        </div>
      </div>
    );
  }
}

export default Landing;
