import React from "react";
import BarChart from "./BarChart";

class BarData extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      year: {},
      yearlyData: {},
    };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e) {
    let years = [];

    if (this.props.DataSets) {
      for (var i = 0; i < this.props.DataSets.length; i++) {
        var date = this.props.DataSets[i].launchDate;
        var dataObject = new Date(date);
        if (dataObject.getFullYear()) {
          years.push(dataObject.getFullYear());
        }
      }
    }
    const tmpYearlyData = years.reduce(function (object, satCount) {
      if (!object[satCount]) {
        object[satCount] = 1;
      } else {
        object[satCount]++;
      }
      return object;
    }, {});

    let title = e.target.value;
    let tmpData = tmpYearlyData
    //console.log(data);
    //let year = Object.keys(tmpData)
    this.setState({
      yearlyData: tmpYearlyData,
      year: Object.keys(tmpData)
    });
  }
  render() {
    let data = this.yearlyData;
    let selectops = (
      <option key={this.year} value={this.year}>
        {this.year}
      </option>
    );

    return (
      <div>
        <div>
          <select
            name="Select year"
            className="year_select"
            onChange={this.handleChange}
          >
            <option>Select Event</option>
            {selectops}
          </select>
        </div>
        <BarChart Datasets = {this.yearlyData} />
      </div>
    );
  }
}
export default BarData;
