import React from "react"
class Main extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            satellites : [],
            isLoaded : false,
        };
    }

    fetchData(url){
        fetch(url,{
            "method": "GET",
            "headers": {
                "x-rapidapi-host": "uphere-space1.p.rapidapi.com",
                "x-rapidapi-key": "7c38e0936bmsh43070bf163c0855p15038cjsn373b4d98e3a6"
            }
        })
        .then((response) => response.json())    
        .then((data) => {
                return data.results.name;
        })
         .then((data) => {
            this.setState({
                satellites : data,
                isLoaded: true,
            });
        })


    }
    componentDidMount() {
   // const url = 'https://uphere-space1.p.rapidapi.com/satellite/list';
     const url = 'https://swapi.co/api/people/'
    this.fetchData(url);
} 
render() {
    const { satellites,isLoaded } = this.state;
    return (
        <React.Fragment>
            {!isLoaded && <p>Loading...</p>}
            satellitesdata = {satellites}                            
             </React.Fragment>     
      )
}

}

export default Main
