import React from "react";
import { HorizontalBar } from "react-chartjs-2";
export default function BarChart(props) {
  //const satlist = [];
  //console.log(props.YearData)
  var label =[];
  var Data =[];
  var year = [];
  if(props.YearData.above){
    var satData = props.YearData.above
  for (var i = 0; i < satData.length; i++) {
    var date = (props.YearData.above[i].launchDate);
    var dataObject = new Date(date);
    //const numbers = props.YearData.above[i].satid.length();
    //satObject.year = dataObject.getFullYear();
    //satObject.month = dataObject.getMonth();
    if(dataObject.getFullYear() > 2010){
    year.push(dataObject.getFullYear());
    }
  }
  var count = year.reduce(function(object,satCount){
    if(!object[satCount]){
      object[satCount] = 1
    }
    else{
      object[satCount]++;
    }
    return object;
  },{}); 

Object.keys(count.object).map((key,i)=>(
  <p key = {i}>
    <span>Year: {key}</span>
    <span>Value: {count.object[key]}</span>
  </p>
))}
  const data = {
    labels: label,
    datasets: [
      {
        label: "Satellite Launch ",
        data: Data,
        backgroundColor: "rgba(255, 215, 0,0.8)",
        borderColor: "rgba(0 ,0 ,238,1)",
        borderWidth: 1,
        hoverBackgroundColor: "rgba(255, 215, 0,0.4)",
        hoverBorderColor: "rgba(0 ,0 ,238,1)",
        barPercentage: 0.5,
        barThickness: 20,    
      },],};
  return (
    <div className="barcharts">
      <h1>Active Satellite</h1>
      <HorizontalBar 
      height={180} 
      data={data}
        options={{
          legend: {
            labels: {
              fontColor: "#fff",
            },},
          }} />
    </div>
  );

}
