import React from "react";
import "./style.css";
import Chart from "./components/Chart"
//import Main from "./components/Main"
import WorldMap  from "./components/WorldMap"
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      nasa: [],
      Data: [],
      SatCat:[],
      isLoaded: false,
    };
  }
  fetchData() {
    const url =
      "https://images-api.nasa.gov/asset/ISS%204K%20Crew%20Earth%20Observations";
    const urls = this.loadSat();
    const url1 = 'https://www.space-track.org/basicspacedata/query/class/satcat/predicates/OBJECT_ID,COUNTRY,%20desc/format/html/emptyresult/show'
    //--------------------fetching url-------------------
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        return data;
      })
      .then((data) => {
        this.setState({
          nasa: data.collection.items[1].href,
          isLoaded: true,
        });
      });
  //--------------------fetching url1-------------------
      fetch(url1,{

        mode: 'no-cors'})
      .then((response) => response.json())
      .then((data) => {
        return data;
      })
      .then((data) => {
        this.setState({
          SatCat: data,
          isLoaded: true,
        });
        console.log(data)
      });
  //--------------------fetching urls----------------------------
    Promise.all(urls.map((url) => fetch(url)))
      .then((responses) => Promise.all(responses.map((r) => r.json())))
      .then((values) => {
        return values;
      })
      .then((data) => {
        this.setState({
          isLoaded: true,
          Data: data,
        });
      });

    // .catch((error) = console.log(error));
  }

  componentDidMount() {
    this.fetchData();
  }

  loadSat() {
    let urls = [];
    for (let id = 2; id <= 13; id++) {
      let url1 =
        "https://www.n2yo.com/rest/v1/satellite/above/41.702/-76.014/0/70/" +
        id +
        "/&apiKey=TY7W6H-2YWZWQ-9W9WEL-4FIH";
      urls.push(url1);
    }
    return urls;
    //console.log(urls)
  }
  //------------------render snd return--------------------
  render() {
    //const { nasa, satellites, category, isLoaded } = this.state;
    const { nasa, Data, isLoaded } = this.state;
   // console.log(Data)
    return (
      <React.Fragment>
        <h1>Satellite Tracker</h1>
        {!isLoaded && <p>Loading...</p>}
        <video autoPlay controls muted loop src={nasa} type="video/mp4" />
        <Chart DataSet = {Data} />

      </React.Fragment>
    );

  }

}

export default App;
