import React from "react";
import BarChart from "./BarChart";

class BarData extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      year: {},
      yearlyData: {},
    };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e) {
    let years = [];

    if (this.props.DataSets) {
      for (var i = 0; i < this.props.DataSets.length; i++) {
        var date = this.props.DataSets[i].launchDate;
        var dataObject = new Date(date);
        if (dataObject.getFullYear()) {
          years.push(dataObject.getFullYear());
        }
      }
    }
    const tmpYearlyData = years.reduce(function (object, satCount) {
      if (!object[satCount]) {
        object[satCount] = 1;
      } else {
        object[satCount]++;
      }
      return object;
    }, {});
    console.log(tmpYearlyData);

    let title = e.target.value;
    //let year = Object.keys(tmpData)
    this.setState({
      yearlyData: tmpYearlyData,
      year: Object.keys(tmpYearlyData),
    });
  }

  render() {
    let data = this.state.yearlyData;
    console.log(data);
    let selectops = (
      <option key={this.state.year.title} value={this.state.year.title}>
        {this.state.year.title}
      </option>
    );

    return (
      <div>
        <div>
          <select
            name="Select year"
            className="year_select"
            onChange={this.handleChange}
          >
            <option>Select Year</option>
            {selectops}
          </select>
        </div>
        <BarChart satdata = {this.state.yearlyData} />
      </div>
    );
  }
}
export default BarData;
