import React from "react";
import BarChart from "./components/BarChart";
export default function Sdata(props) {

  var localYears = [];
  if (props.DataSets.above) {
    var satData = props.DataSets.above;
    for (var i = 0; i < satData.length; i++) {
      var date = satData[i].launchDate;
      var dataObject = new Date(date);
      if (dataObject.getFullYear()) {
        localYears.push(dataObject.getFullYear());
      }
    }
  }
  const yearlyData = localYears.reduce(function (object, satCount) {
    if (!object[satCount]) {
      object[satCount] = 1;
    } else {
      object[satCount]++;
    }
    return object;
  }, {});

  const data = {
    labels: Object.keys(yearlyData),
    datasets: [
      {
        label: "Satellite Count By Years",
        backgroundColor: "rgba(255, 215, 0,0.8)",
        borderColor: "rgba(0 ,0 ,238,1)",
        borderWidth: 1,
        hoverBackgroundColor: "rgba(255, 215, 0,0.4)",
        hoverBorderColor: "rgba(0 ,0 ,238,1)",
        barPercentage: 0.5,
        barThickness: 20,
        data: Object.values(yearlyData),
      },
    ],
  };
  const options = {
    legend: {
      display: true,
      labels: {
        fontColor: "#fff",
      },
    },
    scales: {
      yAxes: [
        {
          scaleLabel: {
            padding: 20,
          },
          ticks: {
            autoSkip: false,
            fontColor: "#fff",
            fontSize: 14,
          },
        },
      ],
      xAxes: [
        {
          ticks: {
            fontColor: "#fff",
            fontSize: 14,
          },
        },
      ],
    },
  };

  return (
    <BarChart data={data} options={options} />
  );
}
