import React from "react";
import { Bar } from "react-chartjs-2";
class BarChart extends React.Component{
  render(){
    let Datasets = this.props.Datasets;
    let data = {};
    let options = {};
   data = {
      labels: Object.keys(Datasets),
      datasets: [
        {
          label: "Satellite Count By Years",
          backgroundColor: "rgba(255, 215, 0,0.8)",
          borderColor: "rgba(0 ,0 ,238,1)",
          borderWidth: 1,
          hoverBackgroundColor: "rgba(255, 215, 0,0.4)",
          hoverBorderColor: "rgba(0 ,0 ,238,1)",
          barPercentage: 0.5,
          barThickness: 20,
          data: Object.values(Datasets),
        },
      ],
    };
   options = {
      legend: {
        display: true,
        labels: {
          fontColor: "#fff",
        },
      },
      scales: {
        yAxes: [
          {
            scaleLabel: {
              padding: 20,
            },
            ticks: {
              autoSkip: false,
              fontColor: "#fff",
              fontSize: 14,
            },
          },
        ],
        xAxes: [
          {
            ticks: {
              fontColor: "#fff",
              fontSize: 14,
            },
          },
        ],
      },
    };

    return(
      <div>
      <h1>Active Satellites</h1>
      <Bar data={data} height={20} width={80} options={options} />
      </div>
    );      
  }
}
export default BarChart;
