import React from "react";
import './style.css';
//import { render } from "react-dom";

//import Header from "./components/Header"
//import Main from "./components/Main"
//import Footer  from "./components/Footer"

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            nasa : [],
            satellites : [],
            isLoaded : false,
        };
    }

    fetchData(url,url1){
        Promise.all([
            fetch(url)
            .then((response) => response.json()),
            fetch(url1)
            .then((response) => response.json())  
        ])       
        .then((data) => {
           //return data;
           //data[0].collection.items[1].href;
           console.log(data[1].results.name,);

        })
        .then((data) => {
            this.setState({
                nasa : data[0].collection.items[1].href,
                satellites : data[1].results.name,
                isLoaded: true,
            });
        })
      // .catch((error) = console.log(error));
    }
    componentDidMount() {
    const url = 'https://images-api.nasa.gov/asset/ISS%204K%20Crew%20Earth%20Observations';
    const url1 = 'https://swapi.dev/api/people/';
    this.fetchData(url,url1);
} 
render() {
    const { nasa,satellites,isLoaded } = this.state;
    return (
        <React.Fragment>
            <h1>Satellite Tracker</h1>
            {!isLoaded && <p>Loading...</p>}

            <video autoPlay controls muted loop
                 src = {nasa} type = "video/mp4" /> 

            <h3>Satellite List = {satellites} </h3>                      
             </React.Fragment> 


      )
}

}
export default App
