import React from "react";
import { HorizontalBar } from "react-chartjs-2";

export default function BarChart(props) {
  const satlist = [];
  //console.log(props.YearData)

  if(props.YearData.above){
    var satData = props.YearData.above

  for (var i = 0; i < satData.length; i++) {
    var satObject = {
      year: "",
      month: "",
      satId: "",
    };
    var date = (props.YearData.above[i].launchDate);
    var dataObject = new Date(date);
    satObject.year = dataObject.getFullYear();
    satObject.month = dataObject.getMonth();
    satObject.satId = (props.YearData.above[i].satid);
    satlist.push(satObject);

    // console.log(data.above[i].launchDate)
    // console.log(data.above[i].satid)
  }
}
const data = {
  labels: satlist.satObject.month,
  datasets: [
    {
      label: "Satellite Launched",
      data: satlist.satObject.satId ,
      backgroundColor: "rgba(255, 215, 0,0.8)",
      borderColor: "rgba(0 ,0 ,238,1)",
      borderWidth: 1,
      hoverBackgroundColor: "rgba(255, 215, 0,0.4)",
      hoverBorderColor: "rgba(0 ,0 ,238,1)",
      barPercentage: 0.5,
      barThickness: 20,
    },
  ],
};

return (
  <div className="BarChart">
    <h1>Active Satellites Details</h1>
    <HorizontalBar
      height={180}
      data={data}
      options={{
        legend: {
          labels: {
            fontColor: "#fff",
          },
        },
        scales: {
          yAxes: [
            {
              scaleLabel: {
                padding: 20,
              },
              ticks: {
                autoSkip: false,
                fontColor: "#fff",
                fontSize: 14,
              },
            },
          ],
          xAxes: [
            {
              ticks: {
                fontColor: "#fff",
                fontSize: 14,
              },
            },
          ],
        },
      }}
    />
  </div>
);
}
