import React from "react";
import { Bar } from "react-chartjs-2";
export default function BarChart(props) {
  var years = [];
  if (props.DataSets.above) {
    var satData = props.DataSets.above;
    for (var i = 0; i < satData.length; i++) {
      var date = satData[i].launchDate;
      var dataObject = new Date(date);
      if (dataObject.getFullYear() > 2010) {
        years.push(dataObject.getFullYear());
      }
    }
  }
  var yearlyData = years.reduce(function (object, satCount) {
    if (!object[satCount]) {
      object[satCount] = 1;
    } else {
      object[satCount]++;
    }
    return object;
  }, {});
  Object.entries(yearlyData).forEach(([key, value]) =>
    console.log(`${key}: ${value}`)
  );
  const data = {
    labels: Object.keys(yearlyData),
    datasets: [
      {
        label: "Satellite Launch ",
        backgroundColor: "rgba(255, 215, 0,0.8)",
        borderColor: "rgba(0 ,0 ,238,1)",
        borderWidth: 1,
        hoverBackgroundColor: "rgba(255, 215, 0,0.4)",
        hoverBorderColor: "rgba(0 ,0 ,238,1)",
        barPercentage: 0.5,
        barThickness: 20,
        data: Object.values(yearlyData),
      }
    ],
  };
  const options = {
    legend: {
      labels: {
        fontColor: "#fff",
      },
    },
    scales: {
      yAxes: [
        {
          scaleLabel: {
            padding: 20,
          },
          ticks: {
            autoSkip: false,
            fontColor: "#fff",
            fontSize: 14,
          },
        },
      ],
      xAxes: [
        {
          ticks: {
            fontColor: "#fff",
            fontSize: 14,
          },},],
    },
  };

  return (
    <div className="barcharts">
      <h1>Active Satellites</h1>
      <Bar
        data={data}
        height={20}
        width = {80}
        options={options}

      />
    </div>
  );
}
