import React,{component} from "react"
import Header from "./components/Header"
import Main from "./components/Main"
import Footer  from "./components/Footer"

class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
            video : []
        };
    }

    fetchdata(url){
        fetch(url)
        .then((response) = response.json())
        .then((data) => {
            return data.collection.items[1];
        })
        .then((data) => {
            this.setState({
                video : data
            });
        })
        .catch((error) = console.log(error));
    }
componentDidMount(){
    const url = 'https://images-api.nasa.gov/asset/ISS%204K%20Crew%20Earth%20Observations';
    this.fetch(url);
}
render(){
    const {video}= this.state;
    return (
        <div>
            <h1>Satellite Tracker</h1>
            <video autoPlay = {true} >
                <source src = {"http://images-assets.nasa.gov/video/ISS 4K Crew Earth Observations/ISS 4K Crew Earth Observations~large.mp4"} type = "video/mp4" />
                Your browser does not support the video tag.
            </video>        
             <Main  />
             <Footer  />          
             </div>     
      )
}

}







export default App
