import React from "react";
import "./style.css";
//import { render } from "react-dom";
//import Header from "./components/Header"
//import Main from "./components/Main"
//import Footer  from "./components/Footer"
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      nasa: [],
      urls: [],
      satellites: [],
      category: [],
      isLoaded: false,
    };
  }
  fetchData() {
    const url =
      "https://images-api.nasa.gov/asset/ISS%204K%20Crew%20Earth%20Observations";

    Promise.all([
      fetch(url).then((response) => response.json()),
      //this.urls.map(url1 => fetch(url1).then((response) => response.json())),
      this.loadSat()
    ])

      .then((data) => {
        //return data;
       // console.log(data);
        // return(satellites.push(data.items[0]))
      })

      .then((data) => {
        this.setState({
          nasa: [], //data[0].collection.items[1].href,
          satellites: [], //data[1].info.satcount,
          category: [], //data[1].info.category,
          isLoaded: true,
        });
      });
    // .catch((error) = console.log(error));
  }
  componentDidMount() {
    this.fetchData();
  }

  loadSat(){
    let urls =[]
    for (let id = 1; id < 10; id++) {
      let url1 =
        "https://www.n2yo.com/rest/v1/satellite/above/41.702/-76.014/0/70/" +
        id +
        "/&apiKey=TY7W6H-2YWZWQ-9W9WEL-4FIH";
      urls.push(url1); 
    }
    //return urls;
    console.log(urls)
  }
  //data = {category:count}
  //const url1 = 'https://uphere-space1.p.rapidapi.com/satellite/list?country=IND&page=1';

  render() {
    const { nasa, satellites, category, isLoaded } = this.state;
    return (
      <React.Fragment>
        <h1>Satellite Tracker</h1>
        {!isLoaded && <p>Loading...</p>}

        <video autoPlay controls muted loop src={nasa} type="video/mp4" />

        <h3>Satellite Count = {satellites} </h3>
        <h3>Category = {category} </h3>
      </React.Fragment>
    );
  }
}
export default App;
